description:
{
  "basicInfo": {
    "fullName": "Eren Yeager",
    "aliases": ["The Attack Titan", "The Founding Titan", "The Usurper"],
    "dateOfBirth": "March 30, 835",
    "placeOfBirth": "Shiganshina District, Wall Maria",
    "currentAge": "19 (at the end of the series)",
    "gender": "Male",
    "nationality": "Eldian",
    "ethnicBackground": "Subject of Ymir",
    "languages": ["Common language of Paradis Island"],
    "currentResidence": "Varies throughout the series"
  },
  "physicalAttributes": {
    "height": "183 cm (6'0\")",
    "weight": "Approximately 82 kg (180 lbs)",
    "bodyType": "Athletic, muscular",
    "eyeColor": "Green (human form), Glowing teal/green (Titan form)",
    "hairColor": "Dark brown",
    "hairType": "Straight",
    "hairLength": "Short initially, grows longer later in the series",
    "skinTone": "Light",
    "facialFeatures": {
      "faceShape": "Oval",
      "noseShape": "Straight",
      "lipShape": "Thin",
      "eyebrowShape": "Straight, often furrowed",
      "cheekbones": "Defined"
    },
    "distinguishingMarks": "Titan transformation marks under eyes when using powers",
    "physicalCondition": "Extremely fit, enhanced by Titan powers"
  },
  "personalityTraits": {
    "dominantTraits": [
      "Determined",
      "Passionate",
      "Vengeful",
      "Protective",
      "Idealistic (early on)",
      "Ruthless (later in series)",
      "Complex"
    ],
    "values": [
      "Freedom",
      "Protecting loved ones",
      "Survival of his people",
      "Eradicating threats to his homeland"
    ],
    "fears": [
      "Losing more loved ones",
      "Failing to achieve his goals",
      "Being caged or having his freedom taken away"
    ],
    "motivations": [
      "Avenging his mother's death",
      "Protecting his friends and people",
      "Achieving freedom for Eldians",
      "Ending the cycle of hatred and violence"
    ]
  },
  "backgroundAndUpbringing": {
    "familyStructure": {
      "parents": [
        {
          "relation": "Father",
          "name": "Grisha Yeager",
          "occupation": "Doctor, Titan Shifter"
        },
        {
          "relation": "Mother",
          "name": "Carla Yeager",
          "occupation": "Homemaker"
        }
      ],
      "adoptiveFamily": [
        {
          "relation": "Adoptive sister",
          "name": "Mikasa Ackerman"
        }
      ]
    },
    "childhoodHome": "Shiganshina District, Wall Maria",
    "economicBackground": "Middle class",
    "childhoodExperiences": [
      "Witnessed his mother being eaten by a Titan",
      "Survived the fall of Wall Maria",
      "Lived as a refugee",
      "Trained in the 104th Training Corps"
    ],
    "significantEvents": [
      "Receiving Titan powers from his father",
      "Discovering the truth about the world outside the walls",
      "Learning about his father's past and the true history of their world"
    ]
  },
  "education": {
    "formalEducation": "Basic education in Shiganshina",
    "militaryTraining": {
      "organization": "104th Training Corps",
      "skills": [
        "Vertical Maneuvering Equipment usage",
        "Hand-to-hand combat",
        "Swordsmanship",
        "Military tactics"
      ],
      "achievement": "Graduated 5th in his class"
    }
  },
  "careerAndProfessionalLife": {
    "militaryService": [
      {
        "title": "Soldier",
        "organization": "Survey Corps",
        "duration": "From graduation until becoming a fugitive"
      },
      {
        "title": "Titan Shifter",
        "specialAbilities": [
          "Attack Titan",
          "Founding Titan",
          "War Hammer Titan (later acquired)"
        ]
      }
    ],
    "achievements": [
      "Sealed the hole in Wall Rose using his Titan powers",
      "Key figure in reclaiming Wall Maria",
      "Accessed the memories of past Titan holders",
      "Initiated the Rumbling"
    ]
  },
  "relationshipsAndSocialLife": {
    "closeAllies": [
      "Mikasa Ackerman (adoptive sister, complex relationship)",
      "Armin Arlert (best friend)",
      "Jean Kirstein",
      "Connie Springer",
      "Sasha Blouse"
    ],
    "mentors": [
      "Levi Ackerman",
      "Hange Zoë"
    ],
    "rivals": [
      "Reiner Braun",
      "Zeke Yeager (half-brother)"
    ]
  },
  "skillsAndAbilities": {
    "combatSkills": [
      "Expert in using Vertical Maneuvering Equipment",
      "Skilled hand-to-hand combatant",
      "Proficient with blades"
    ],
    "titanPowers": [
      "Ability to transform into the Attack Titan",
      "Control over other Titans (Founding Titan power)",
      "Access to memories of past and future Attack Titan holders",
      "Hardening ability",
      "War Hammer Titan abilities (later acquired)"
    ],
    "intellectualAbilities": [
      "Strategic thinking",
      "Strong willpower",
      "Ability to inspire and lead others"
    ]
  },
  "beliefsAndValues": {
    "ideology": "Initially focused on freedom and protecting humanity, later shifts to more extreme measures for Eldian freedom",
    "moralCode": "Complex and evolving, willing to take drastic actions for what he believes is right",
    "viewOnHumanity": "Initially hopeful, becomes increasingly cynical and ruthless"
  },
  "goalsAndAspirations": {
    "initialGoals": [
      "Eradicate all Titans",
      "Explore the world outside the walls"
    ],
    "laterGoals": [
      "Protect Paradis Island at all costs",
      "Break the cycle of hatred between Eldians and the world",
      "Ensure the freedom of his people through extreme measures"
    ]
  },
  "challengesAndObstacles": {
    "internalConflicts": [
      "Struggling with the weight of his powers and responsibilities",
      "Balancing his desire for freedom with the cost of his actions",
      "Coping with the knowledge of future memories"
    ],
    "externalConflicts": [
      "Fighting against enemy nations and Titan shifters",
      "Conflicting ideologies with former allies",
      "The burden of Paradis Island's fate"
    ]
  },
  "communicationStyle": {
    "speakingManner": "Passionate and intense, often driven by emotion",
    "expressionOfIdeas": "Direct and uncompromising, especially about freedom and his goals",
    "nonverbalCommunication": {
      "facialExpressions": "Intense, often showing determination or anger",
      "bodyLanguage": "Confident and assertive, can be intimidating"
    },
    "interactionStyle": "Can be abrasive and confrontational, especially when challenged",
    "speechPatterns": {
      "catchphrases": [
        "I'll destroy them all. Every last one of those animals that's on this earth!",
        "If you don't fight, you can't win!"
      ]
    }
  },
  "characterDevelopment": {
    "majorChanges": [
      "From idealistic youth to morally grey antagonist",
      "Increasing willingness to take extreme measures",
      "Growing detachment from former allies"
    ],
    "catalystsForChange": [
      "Learning the truth about the outside world",
      "Accessing memories of past and future",
      "The weight of responsibility for Paradis Island's fate"
    ]
  },
  "legacyAndImpact": {
    "influenceOnWorld": "Drastically altered the global landscape and power dynamics",
    "impactOnOthers": "Inspired loyalty and opposition in equal measure",
    "historicalSignificance": "Key figure in reshaping the world order and Eldian history"
  }
}

