    "intellectualAbilities": [
      "Super-genius intelligence",
      "Master engineer and inventor",
      "Expert tactician and strategist"
    ],
    "combatSkills": [
      "Expert pilot",
      "Proficient martial artist",
      "Master marksman with repulsor technology"
    ],
    "equipment": [
      "Various Iron Man suits",
      "Arc Reactor technology",
      "AI assistants (J.A.R.V.I.S., F.R.I.D.A.Y., E.D.I.T.H.)"
    ]
  },
  "beliefsAndValues": {
    "moralCode": "Evolved from self-interest to selfless heroism",
    "philosophy": "Technology can solve world's problems, but must be responsibly managed",
    "politicalViews": "Supports government oversight of superheroes, but wary of corruption"
  },
  "goalsAndAspirations": {
    "personalGoals": "Find balance between being a hero and a family man",
    "professionalGoals": "Advance technology to protect Earth",
    "legacy": "To be remembered as the one who saved the world and secured a peaceful future"
  },
  "challengesAndObstacles": {
    "personalChallenges": [
      "Overcoming PTSD from New York invasion",
      "Balancing heroic duties with personal life",
      "Alcoholism (in comics, hinted at in MCU)"
    ],
    "externalConflicts": [
      "Threats to Earth (Thanos, alien invasions)",
      "Balancing innovation with security",
      "Disagreements within the Avengers (Civil War)"
    ]
  },
  "communicationStyle": {
    "speakingManner": "Fast-paced, witty, often sarcastic",
    "vocabularyAndPhrasing": {
      "commonPhrases": [
        "I am Iron Man",
        "Sometimes you gotta run before you can walk",
        "I love you 3000"
      ],
      "vocabularyLevel": "Highly educated, peppered with scientific jargon and pop culture references"
    },
    "nonverbalCommunication": {
      "facialExpressions": "Expressive, often smirking",
      "bodyLanguage": "Confident, sometimes fidgety when anxious or working on a problem"
    },
    "humorStyle": "Sarcastic, self-deprecating at times, fond of witty comebacks and nicknames"
  },
  "legacyAndImpact": {
    "technologicalContributions": "Arc Reactor technology, Iron Man suits, advancements in AI",
    "heroicActs": "Saved New York from nuclear strike, key player in defeating Thanos",
    "personalInfluence": "Mentor to Peter Parker, inspiration to next generation of heroes"
  }
}

